import { useState } from "react";

import io from "socket.io-client";
const socket = io("http://localhost:3001");

function App() {
  socket.on("chat", (data) => {
    console.log(data);
  });

  const [Data, setData] = useState("");
  console.log(Data);

  return (
    <div className="App">
      <form
        style={{ display: "flex", justifyContent: "center", marginTop: "5rem" }}
      >
        <input
          type="text"
          // value={name}
          onChange={(e) => setData(e.target.value)}
        />
        <button
          onClick={(e) => {
            e.preventDefault();
            socket.emit("chat_data", Data);
            console.log(Data);
          }}
        >
          sent
        </button>
      </form>
    </div>
  );
}

export default App;
