import { useEffect, useState } from "react";
import "./css/App.css";
import io from "socket.io-client";
import ScrollToBottom from "react-scroll-to-bottom";

const socket = io.connect("http://localhost:4001");

function App() {
  //STATE//
  const [name, setName] = useState("");
  const [chat, setChat] = useState(false);
  const [sendMessage, setSendMessage] = useState("");
  const [allChat, setAllChat] = useState([]);
  const room_id = 1;

  const UserData = {
    room_id: room_id,
    message: sendMessage,
    author: name,
    time:
      new Date(Date.now()).getHours() + ":" + new Date(Date.now()).getMinutes(),
  };

  const sentMessage = async () => {
    if (sendMessage !== "") {
      await socket.emit("myData", UserData);
      setAllChat((list) => [...list, UserData]);
      setSendMessage("");
    }
  };

  const joinRoom = () => {
    if (name !== "" && name.length >= 3) {
      setChat(true);
      socket.emit("join_room", name, room_id);
    }
  };

  useEffect(() => {
    socket.on("dataBack", (data) => {
      setAllChat((list) => [...list, data]);
    });
  }, [socket]);

  const handleKeyPress = (event) => {
    if (event.keyCode === 13 || event.which === 13) {
      sentMessage();
    }
  };

  const HandleJoinRoom = (event) => {
    if (event.keyCode === 13 || event.which === 13) {
      joinRoom();
    }
  };

  return (
    <div>
      {!chat ? (
        <div className="container">
          <div className="join-page">
            <h2>JOIN ROOM</h2>
            <div className="box-wrapper">
              <input
                placeholder="What's your name ?"
                id="join-input"
                onChange={(e) => {
                  setName(e.target.value);
                }}
                onKeyPress={HandleJoinRoom}
              />
              <button id="join-btn" onClick={joinRoom}>
                Join Chat
              </button>
            </div>
          </div>
        </div>
      ) : (
        <div className="container">
          <h1>Chat Room</h1>
          <div className="chat-box">
            <ScrollToBottom className="box-wrapper">
              {allChat.map((message) => (
                <div
                  className={
                    name !== message.author ? "message-item" : "item-author"
                  }
                >
                  <h3 className={name !== message.author ? "h3" : "h3-author"}>
                    {message.message}
                  </h3>
                  <div
                    style={{
                      display: "flex",
                    }}
                  >
                    <h4>{name === message.author ? "You" : message.author}</h4>
                    <h5>{message.time}</h5>
                  </div>
                </div>
              ))}
            </ScrollToBottom>
            <div className="sent">
              <input
                placeholder="Write Something ..."
                value={sendMessage}
                onChange={(e) => {
                  setSendMessage(e.target.value);
                }}
                onKeyPress={handleKeyPress}
              />
              <button onClick={sentMessage}>Sent</button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
